
public class logicaloperaters {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		 int a =50;
		 int b = 20;
		 int c = 30;
		 System.out.println(a>b && b>c);
		 System.out.println (a>b || b>c);
		 
		 a=10;
		 a++;  //a=a+1
		 System.out.println(a);
		 b=20;
		 b--;
		 
		 System.out.print(a+ --b);
		
		

	}

}

